mapper.insert:

    SELECT
           SEQ_NOTICE_NO.CURRVAL
      FROM DUAL

INSERT
  INTO NOTICE_FILE
(
  FILE_NO
, ORIGIN_NAME
, FILE_NAME
, SAVED_PATH
, NOTICE_NO
)
VALUES
(
  SEQ_NOTICE_FILE_NO.NEXTVAL
, #{ originName }
, #{ fileName }
, #{ savedPath }
, #{ noticeNo }
)

mapper.delete:
DELETE
  FROM NOTICE
 WHERE NOTICE_NO = #{ noticeNo }

DELETE
  FROM NOTICE_FILE
 WHERE NOTICE_NO = #{ noticeNo }

mapper.update:
UPDATE
       NOTICE


        , NOTICE_NAME = #{ noticeName }


        , NOTICE_CONTENT = #{ noticeContent }

        , NOTICE_MODIFY_DATE = TO_CHAR(SYSDATE,'yyyy-MM-dd HH24:MI:SS')

 WHERE NOTICE_NO = #{ noticeNo }

