mapper.update:
UPDATE
       NOTICE A
   SET A.NOTICE_COUNT = (SELECT B.NOTICE_COUNT
                           FROM NOTICE B
                          WHERE B.NOTICE_NO = #{ noticeNo }
                        ) + 1
WHERE A.NOTICE_NO = #{ noticeNo }
